
function BaseConnectionLine(axis, x, y1, y2, width, color, dashArray) {
    this.axis = axis;
    this.x = x;
    this.y1 = y1;
    this.y2 = y2;
    this.width = width;
    this.color = color;
    this.dashArray = dashArray;
    this.line = null;
    this.isNew = false;
};

BaseConnectionLine.prototype = {
    render: function() {
        var chart = this.axis.chart,
            renderer = chart.renderer;
        var linePath = ['M', this.x , this.y1 , 'L' , this.x , this.y2];
        if(!this.line) {
            this.line = renderer.path(linePath).attr({
                'stroke': this.color,
                'stroke-width': this.width,
                'stroke-dasharray': this.dashArray
            }).add(this.axis.baseLineGroup);
        } else {
            this.line.animate({
                d: linePath
            })
        }
        this.isNew = false;
    }
};