import React from 'react';
import '../build/docs.css';
import 'apm-icon/iconfont.css';

import Navbar from './partials/navbar.jsx';
import Sidebar from './partials/sidebar.jsx';

class App extends React.Component {
  state = {
    collapsed: false
  };

  handleSidebarToggle(collapsed) {
    this.setState({ collapsed });
  }

  render() {
    return (
      <div className={`flex-col-1 flex-repair ${ this.state.collapsed ? 'collapsed': null }`}>
        <Navbar />
        <div className="flex-1 flex-repair">
          <Sidebar onToggle={ this.handleSidebarToggle.bind(this) } collapsed={ this.state.collapsed } />

          <div id="container" className="flex-1">
            { this.props.children }
          </div>
        </div>
      </div>
    );
  }
}

export default App;
