import React from 'react';

class Page extends React.Component {
  render() {
    return (
      <div className="flex-col-1 flex-repair">
        <div className="page-header">
          { this.props.right }
          {
            this.props.alias === '/' ? (
              <ul className="breadcrumb">
                <li className="active">扶뒬못</li>
              </ul>
            ) : (
              <ul className="breadcrumb">
                <li><a href="#/">扶뒬못</a> <i className="icon">&#xe62e;</i></li>
                <li className="active">{ this.props.alias }</li>
              </ul>
            )
          }
        </div>

        <div className="root-view flex-col-1">
          { this.props.children }
        </div>
      </div>
    );
  }
}

module.exports = Page;
