import React from 'react';
import Page from '../components/page/index.jsx';
import { Popover, OverlayTrigger } from '../../build/components';
import ParamTable from '../components/param-table/index.jsx';

class PopoverExample extends React.Component {
  static defaultProps = {
    popoverClassName: 'popover-mini-reverse'
  };

  render() {
    let text = this.props.trigger === 'click' ? '点我' : '鼠标移入',
        popover = (
          <Popover className={ this.props.popoverClassName }>
            <strong>Holy guacamole!</strong> Check this info.
          </Popover>
        );

    return (
      <div className="relative">
        <OverlayTrigger container={ this } rootClose {...this.props} overlay={ popover } placement="right">
          <button className="btn btn-danger">{ text }（右）</button>
        </OverlayTrigger>

        <OverlayTrigger container={ this } rootClose {...this.props} overlay={ popover } placement="top">
          <button className="btn btn-danger">{ text }（上）</button>
        </OverlayTrigger>

        <OverlayTrigger container={ this } rootClose {...this.props} overlay={ popover } placement="bottom">
          <button className="btn btn-danger">{ text }（下）</button>
        </OverlayTrigger>

        <OverlayTrigger container={ this } rootClose {...this.props} overlay={ popover } placement="left">
          <button className="btn btn-danger">{ text }（左）</button>
        </OverlayTrigger>
      </div>
    );
  }
}

