import React from 'react';
import { Router, Route, IndexRoute, hashHistory } from 'react-router';
import Overview from './examples/overview.jsx';
import ButtonExamples from './examples/button.jsx';
import PaginatorExamples from './examples/paginator.jsx';
import NavTabExamples from './examples/nav-tab.jsx';
import TagExamples from './examples/tag.jsx';
import FlexibleExamples from './examples/flexible.jsx';
import TypeExamples from './examples/type.jsx';
import AlertExamples from './examples/alert.jsx';
import PopoverExamples from './examples/popover.jsx';
import CalendarExamples from './examples/calendar.jsx';
import BreadcrumbExamples from './examples/breadcrumb.jsx';
import ProgressExamples from './examples/progress.jsx';
import IncrementExamples from './examples/increment.jsx';
import ModalExamples from './examples/modal.jsx';
import TableExamples from './examples/table.jsx';
import FormExamples from './examples/form.jsx';
import SearchExamples from './examples/search.jsx';
import DropdownExamples from './examples/dropdown.jsx';
import VerificationExamples from './examples/verification.jsx';
import PanelExamples from './examples/panel.jsx';
import HeadingExamples from './examples/heading.jsx';
import CodeExamples from './examples/code.jsx';
import TriggerExamples from './examples/trigger.jsx';
import GridExamples from './examples/grid.jsx';
import LoadingExamples from './examples/loading.jsx';
import LayoutExamples from './examples/layout.jsx';
import ListExamples from './examples/list.jsx';
import SelectExamples from './examples/select.jsx';
import NavStepExamples from './examples/nav-step.jsx';
import ToolsExamples from './examples/tools.jsx';

import IndexView from './views/index.jsx';

import App from './app.jsx';

const routes = (
  <Router history={ hashHistory }>
    <Route path="/" component={ IndexView } />

    <Route path="/docs" component={ App }>
      <IndexRoute component={ Overview } />

      <Route path="button" component={ ButtonExamples } />
      <Route path="paginator" component={ PaginatorExamples } />
      <Route path="tag" component={ TagExamples } />
      <Route path="nav-tab" component={ NavTabExamples } />
      <Route path="flexible" component={ FlexibleExamples } />
      <Route path="type" component={ TypeExamples } />
      <Route path="alert" component={ AlertExamples } />
      <Route path="popover" component={ PopoverExamples } />
      <Route path="calendar" component={ CalendarExamples } />
      <Route path="breadcrumb" component={ BreadcrumbExamples } />
      <Route path="progress" component={ ProgressExamples } />
      <Route path="increment" component={ IncrementExamples } />
      <Route path="modal" component={ ModalExamples } />
      <Route path="table" component={ TableExamples } />
      <Route path="form" component={ FormExamples } />
      <Route path="search" component={ SearchExamples } />
      <Route path="dropdown" component={ DropdownExamples } />
      <Route path="verification" component={ VerificationExamples } />
      <Route path="panel" component={ PanelExamples } />
      <Route path="heading" component={ HeadingExamples } />
      <Route path="code" component={ CodeExamples } />
      <Route path="trigger" component={ TriggerExamples } />
      <Route path="grid" component={ GridExamples } />
      <Route path="loading" component={ LoadingExamples } />
      <Route path="layout" component={ LayoutExamples } />
      <Route path="list" component={ ListExamples } />
      <Route path="select" component={ SelectExamples } />
      <Route path="nav-step" component={ NavStepExamples } />
      <Route path="tools" component={ ToolsExamples } />
    </Route>
  </Router>
);

module.exports = routes;
