import React, { Component } from 'react';
import cx from 'classnames';

export default class ControllBox extends Component {
  static defaultProps = {
    rights: [],
    children: [],
    className: 'tool-icon-text'
  };

  render() {
    return (
      <div className={ cx('table-tools clearfix', this.props.className) }>
        <div className="pull-right flex-item-middle">
          {
            this.props.rights.map(function (item, key) {
              return Object.assign({}, item, {key});
            })
          }
        </div>
        <div className="control-box-main">
          { this.props.children }
        </div>
      </div>
    )
  }
}
