import React from 'react';
import { storiesOf, action } from '@kadira/storybook';
import { Modal } from '../build/components';
import NavTabModal from './components/nav-tab-modal.js';

const handleMountModal = () => {
  Modal.mount({
    title: '模态框示例',
    draggable: false
  });
};

const handleMountNavTabModal = () => {
  NavTabModal.mount({
    draggable: false,
    headerClassName: 'modal-tabs'
  });
};

module.exports = storiesOf('模态框', module)
  .add('默认风格', () => (
    <div className="container">
      <a href="javascript:;" className="btn btn-success" onClick={ handleMountModal }>点我弹出默认风格的模态框</a>
    </div>
  ))
  .add('带选项卡', () => (
    <div className="container">
      <a href="javascript:;" className="btn btn-success" onClick={ handleMountNavTabModal }>点我弹出带选项卡的模态框</a>
    </div>
  ));
